mapper.select:
SELECT so.id, shop_id shopId, state, name, type, discount, start_time startTime, end_time endTime, scope FROM t_special_offers so
so.shop_id = #{shopId}
and so.type = #{type}
and so.state = #{state}
and TO_DAYS(so.start_time)=TO_DAYS(#{name})
order by so.state limit #{beginNum}, #{limit}

SELECT      
    count(*)
FROM
    t_special_offers so

    so.shop_id = #{shopId}

        and so.type = #{type}


        and so.state = #{state} 


        and  TO_DAYS(so.start_time)=TO_DAYS(#{name})

mapper.update:
