mapper.select:
select tgm.id , tgm.serial_number serialNumber, ts.name name , tgm.total_money totalMoney , tgm.order_date orderDate , tgm.delivery_date deliveryDate , tgm.remark , tgm.goodstraffic_state goodstrafficState , tgm.transportation_state transportationState , tgm.settlement_status settlementStatus from t_goodstraffic_management tgm, t_shop ts
tgm.goodstraffic_state = 1 and tgm.shop_id = ts.id
and tgm.shop_id = #{shopId}
and tgm.order_date between #{startdate} and #{enddate}
order by tgm.id desc limit #{page},#{limit}

select count(tgm.id) count from t_goodstraffic_management tgm, t_shop ts
tgm.goodstraffic_state = 1 and tgm.shop_id = ts.id
and tgm.shop_id = #{shopId}
and tgm.order_date between #{startdate} and #{enddate}

select tgm.id, ts.name shipmentsShopName, tgm.total_money totalMoney, tgm.delivery_date deliveryDate, tgm.remark, tgm.serial_number serialNumber from t_goodstraffic_management tgm, t_shop ts
tgm.receiving_shop_id = #{shopId} and tgm.shipments_shop_id = ts.id
and tgm.order_date between #{startdate} and #{enddate}
and tgm.transportation_state = #{transportationState}
order by tgm.id desc limit #{page},#{limit}

select count(tgm.id) count from t_goodstraffic_management tgm, t_shop ts
tgm.receiving_shop_id = #{shopId} and tgm.shipments_shop_id = ts.id
and tgm.order_date between #{startdate} and #{enddate}
and tgm.transportation_state = #{transportationState}

select tgm.id, ts.name receivingShopName, tgm.total_money totalMoney, tgm.delivery_date deliveryDate, tgm.remark, tgm.serial_number serialNumber from t_goodstraffic_management tgm, t_shop ts
tgm.shipments_shop_id = #{shopId} and tgm.receiving_shop_id = ts.id
and tgm.order_date between #{startdate} and #{enddate}
and tgm.transportation_state = #{transportationState}
order by tgm.id desc limit #{page},#{limit}

select count(tgm.id) count from t_goodstraffic_management tgm, t_shop ts
tgm.shipments_shop_id = #{shopId} and tgm.receiving_shop_id = ts.id
and tgm.order_date between #{startdate} and #{enddate}
and tgm.transportation_state = #{transportationState}

mapper.update:
update 
    t_goodstraffic_management        
set
    settlement_status = #{settlementStatus}
where 
    id = #{id}

mapper.select:
select tgm.id, tgm.total_money totalMoney, DATE_FORMAT( tgm.order_date ,'%Y-%m-%d %H:%i:%s') orderDate , DATE_FORMAT( tgm.delivery_date ,'%Y-%m-%d %H:%i:%s') deliveryDate, tgm.remark from t_goodstraffic_management tgm, t_shop ts
tgm.goodstraffic_state = 1 and tgm.shop_id = #{shopId} and tgm.shop_id = ts.id
and tgm.order_date between #{startdate} and #{enddate}
order by tgm.id desc limit #{page},#{limit}
