mapper.select:
select i.id, i.quantity, p.name productName, p.pleased, pt.product_type productTypeName, i.inventory_warning inventoryWarning from t_inventory i , t_product p , t_product_type pt
i.product_id = p.id and p.product_type_id = pt.id
and i.shop_id = #{shopId}
and pt.id = #{productTypeId}
order by i.id limit #{page},#{limit}

select count(i.id) from t_inventory i , t_product p , t_product_type pt
i.product_id = p.id and p.product_type_id = pt.id
and i.shop_id = #{shopId}
and pt.id = #{productTypeId}

mapper.update:
update
    t_inventory


    quantity=quantity+#{quantity}


where
    product_id = #{productId}
AND shop_id = #{shopId}

update
    t_inventory


    quantity=quantity-#{quantity}


where
    product_id = #{productId}
AND shop_id = #{shopId}
