mapper.select:
SELECT rpr.id id, r.name roleName, rpr.role_id roleId, ps.id permissionId, ps.name permissionName FROM t_role r, t_permission ps, t_role_permission_relationship rpr
r.id = rpr.role_id AND rpr.permission_id = ps.id
AND rpr.role_id = #{roleId}

SELECT COUNT(DISTINCT tb.id) count FROM ( SELECT DISTINCT u.id FROM t_user u, t_role r, t_user_role_relationship urr
u.id = urr.user_id AND urr.role_id = r.id
and u.shop_id = #{shopId}
and u.phone like "%"#{phones}"%"
)as tb

mapper.update:
UPDATE t_user u


		u.state = #{state}


WHERE
 	u.id = #{userId}
