mapper.update:
update t_product
set can_use=2
WHERE id=#{productId}

mapper.select:
select id, name, pleased, barcode barCode, sale_price salePrice, member_price memberPrice, image, state, specification, color, inventory from t_product WHERE shop_id=#{shopId} and can_use=1
and product_type_id=#{productTypeId}
and name like "%"#{productName}"%"
limit #{page},#{limit}


SELECT
   count(id)
from t_product
where can_use=1 and shop_id=#{shopId}

    and product_type_id=#{productTypeId}

SELECT id, name, pleased, sale_price salePrice, barcode barCode, member_price memberPrice, image, state, specification, color, inventory
from t_product where can_use=1 and shop_id=#{shopId} and barcode=#{barCode}
and id!=#{id}
