mapper.select:
SELECT DISTINCT p.area_id provid, p.title provice, c.area_id cityid, c.title city, a.area_id areaid, a.title title, t.name name, t.create_time createTime, t.general_scope generalScope FROM t_dm_area c, t_dm_area a, t_dm_area p, t_shop t
WHERE c.area_id = a.pid AND c.pid = p.area_id AND t.area = a.area_id
AND t.id = #{id}

SELECT id, name, type FROM t_shop
id = #{id}
type = #{type}
